
def razbi_stevilo(n, st):
    s = []
    st = str(st)
    while len(st) > n:
        stevilo = st[:n]
        s.append(stevilo)
        st = st[1:]
    return s #seznam 13 mestnih steil podanih v nizu

def razbi_stevke(n):
    sez_stevk = []
    for stevka in n:
        sez_stevk.append(int(stevka))
    sez_stevk.sort()
    return sez_stevk


def max_zmnozek(n, st):
    s = razbi_stevilo(n, st)
    sez_stevil = []
    for ste in s:
        sez_stevil.append(razbi_stevke(ste))
    maxi = max(sez_stevil)
    zmnozek = 1
    for stevka in maxi:
        zmnozek *= stevka
    return zmnozek

